// This version of the code is updated to get the file name from the
// node named 'IF' in the workflow.

// --- HELPER FUNCTIONS ---

function sanitizeName(name) {
  if (!name) {
    return null;
  }

  const lastDotIndex = name.lastIndexOf('.');
  const baseName = (lastDotIndex > 0) ? name.substring(0, lastDotIndex) : name;

  return baseName.toString().toLowerCase()
    .replace(/\s+/g, '_')
    .replace(/[^\w]/g, '_')
    .replace(/_{2,}/g, '_')
    .replace(/^_+|_+$/g, '');
}

// Converts Excel serial number to a JavaScript Date object
function excelSerialToDate(serial) {
  if (typeof serial !== 'number' || serial < 1) {
    return null;
  }
  const utc_days  = Math.floor(serial - 25569);
  const date = new Date(utc_days * 86400 * 1000);
  const timezoneOffset = date.getTimezoneOffset() * 60000;
  return new Date(date.getTime() + timezoneOffset);
}


function inferDataType(header, values) {
  // Check the column header for date-related keywords first.
  const sanitizedHeader = sanitizeName(header);
  const dateKeywords = ['date', 'day', 'time', 'hire', 'join', 'created', 'updated', 'modified'];
  const headerIsDateHint = dateKeywords.some(keyword => sanitizedHeader.includes(keyword));

  let isInteger = true;
  let isNumeric = true;
  let isDate = true;
  let isExcelSerial = true;
  const dateRegex = /^\d{4}-\d{2}-\d{2}|\d{1,2}\/\d{1,2}\/\d{4}/;

  for (const value of values) {
    if (value === null || value === undefined || value === '') continue;
    const strValue = String(value);
    if (isInteger && !/^-?\d+$/.test(strValue)) isInteger = false;
    if (isNumeric && isNaN(Number(strValue.replace(/[$,]/g, '')))) isNumeric = false;

    if (isDate && (!dateRegex.test(strValue) || isNaN(Date.parse(strValue)))) {
        isDate = false;
    }

    if (isExcelSerial) {
        const numValue = Number(strValue);
        if (isNaN(numValue) || numValue < 1) {
            isExcelSerial = false;
        }
    }
  }

  // PRIMARY LOGIC: If the header suggests a date AND the content looks like a date, we are confident.
  if (headerIsDateHint && (isDate || isExcelSerial)) {
      if (isDate) return 'DATE';
      if (isExcelSerial) return 'EXCEL_SERIAL_DATE';
  }

  // CORRECTED FALLBACK LOGIC: If the header doesn't provide a hint, be more specific.
  // This prevents misidentifying numeric IDs as dates.
  if (isInteger) return 'INTEGER';
  if (isNumeric) return 'NUMERIC';
  if (isDate) return 'DATE'; // Standard dates without a keyword header
  if (isExcelSerial) return 'EXCEL_SERIAL_DATE'; // Excel dates without a keyword header
  return 'TEXT';
}

// --- MAIN LOGIC ---

const fileName = $('If').item.json.name;
const tableName = sanitizeName(fileName);

const items = $input.all();

if (items.length === 0) {
  return [{ json: { error: "Input data is missing or empty." } }];
}

const firstItem = items[0].json;
const headers = Object.keys(firstItem);
const samples = items.map(item => item.json);

const schema = {};
const columnDefinitions = [];

for (const header of headers) {
  const sanitizedHeader = sanitizeName(header);
  const columnValues = samples.map(sample => sample[header]);
  // Pass the original header to the inference function
  let dataType = inferDataType(header, columnValues);

  if (dataType === 'EXCEL_SERIAL_DATE') {
      schema[header] = dataType;
      columnDefinitions.push(`"${sanitizedHeader}" DATE`);
  } else {
      schema[header] = dataType;
      columnDefinitions.push(`"${sanitizedHeader}" ${dataType}`);
  }
}

const createQuery = `CREATE TABLE IF NOT EXISTS public."${tableName}" (
  "n8n_id" SERIAL PRIMARY KEY,
  ${columnDefinitions.join(',\n  ')}
);`;

const cleanedData = items.map(item => {
  const newRow = {};
  for (const header of headers) {
    const sanitizedHeader = sanitizeName(header);
    const dataType = schema[header];
    let value = item.json[header];

    if (dataType === 'EXCEL_SERIAL_DATE' && typeof value === 'number') {
        const date = excelSerialToDate(value);
        value = date ? date.toISOString().split('T')[0] : null; // Format as YYYY-MM-DD
    } else if ((dataType === 'INTEGER' || dataType === 'NUMERIC') && typeof value === 'string') {
      value = Number(String(value).replace(/[$,]/g, ''));
      if (isNaN(value)) value = null;
    } else if (dataType === 'DATE' && value) {
      const date = new Date(value);
      if (!isNaN(date.getTime())) {
        value = date.toISOString();
      } else {
        value = null;
      }
    }
    newRow[sanitizedHeader] = value;
  }
  return newRow;
});

return [{
  json: {
    createQuery,
    tableName,
    cleanedData,
  }
}];

// Helper function to sanitize names - UPDATED to remove file extensions
function sanitizeName(name) {
  if (!name) {
    return null;
  }

  // Remove the file extension before sanitizing
  const lastDotIndex = name.lastIndexOf('.');
  const baseName = (lastDotIndex > 0) ? name.substring(0, lastDotIndex) : name;

  // Sanitize the base name
  return baseName.toString().toLowerCase()
    .replace(/\s+/g, '_')
    .replace(/[^\w]/g, '_')
    .replace(/_{2,}/g, '_')
    .replace(/^_+|_+$/g, '');
}

const allItems = $input.all();

// Create empty lists to sort the merged data into
const driveItems = [];
const postgresItems = [];

// Loop through all merged items and sort them
for (const item of allItems) {
  if (item.json.name) {
    driveItems.push(item);
  } else if (item.json.table_name) {
    postgresItems.push(item);
  }
}

// 1. Create a clean list of table names that SHOULD exist
const expectedTableNames = driveItems
  .map(item => sanitizeName(item.json.name))
  .filter(Boolean); // Filter out any nulls from nameless files

// 2. Create a clean list of table names that ACTUALLY exist
const actualTableNames = postgresItems.map(item => item.json.table_name);

// 3. Find the tables that are in Postgres but NOT in Google Drive anymore
const orphanTables = actualTableNames.filter(tableName => {
  // UPDATED LOGIC: Create a list of tables to always ignore
  const ignoreList = ['n8n_chat_histories'];

  // Check if the table is a system table or in our explicit ignore list
  const isSystemTable = tableName.startsWith('pg_') || ignoreList.includes(tableName);

  if (isSystemTable) {
    return false; // If it's a system table, it's NOT an orphan.
  }

  // If it's not a system table, check if it's missing from our Drive files.
  return !expectedTableNames.includes(tableName);
});

// 4. Return a list of the orphan tables to be deleted
if (orphanTables.length === 0) {
  return [{ json: { status: "No orphan tables found." } }];
}

return orphanTables.map(tableName => ({ json: { tableName } }));

// 1. Separate the inputs into two lists: one for Drive, one for LightRAG.
const driveItems = [];
let lightragData = null;

for (const item of items) {
  if (item.json.name) {
    // This looks like a Google Drive item
    driveItems.push(item.json);
  } else if (item.json.statuses) {
    // This looks like the LightRAG item
    lightragData = item.json;
  }
}

// 2. Create a simple list of all valid file names from Google Drive.
const driveFileNames = driveItems.map(file => file.name);

// 3. Get the list of all documents currently in LightRAG.
const lightragDocs = lightragData?.statuses?.processed || [];

// 4. Filter the LightRAG list to find the orphans.
const orphans = lightragDocs.filter(doc => !driveFileNames.includes(doc.file_path));

// 5. Return a new list containing ONLY THE IDs of the orphan documents.
if (orphans.length === 0) {
  return [{ json: { status: "No orphans found." } }];
}

return orphans.map(orphan => ({ json: { id: orphan.id } }));

