// Sanitizes names for files and columns.
function sanitizeName(name) {
  if (!name) {
    return null;
  }
  const lastDotIndex = name.lastIndexOf('.');
  const baseName = (lastDotIndex > 0) ? name.substring(0, lastDotIndex) : name;
