    .replace(/[^\w]/g, '_')
    .replace(/_{2,}/g, '_')
    .replace(/^_+|_+$/g, '');
}

// Converts Excel serial number to a JavaScript Date object.
function excelSerialToDate(serial) {
  if (typeof serial !== 'number' || serial < 1) {
    return null;
  }
  const utc_days  = Math.floor(serial - 25569);
  const date = new Date(utc_days * 86400 * 1000);
  const timezoneOffset = date.getTimezoneOffset() * 60000;
  return new Date(date.getTime() + timezoneOffset);
}

// Infers data type by checking the header and a sample of values.
function inferDataType(header, values) {
  const sanitizedHeader = sanitizeName(header);
  const dateKeywords = ['date', 'day', 'time', 'hire', 'join', 'created', 'updated', 'modified'];
  const headerIsDateHint = dateKeywords.some(keyword => sanitizedHeader.includes(keyword));

  let isDate = true;
  let isExcelSerial = true;
  const dateRegex = /^\d{4}-\d{2}-\d{2}|\d{1,2}\/\d{1,2}\/\d{4}/;

  for (const value of values) {
    if (value === null || value === undefined || value === '') continue;
    const strValue = String(value);

    if (isDate && (!dateRegex.test(strValue) || isNaN(Date.parse(strValue)))) {
        isDate = false;
    }

    if (isExcelSerial) {
        const numValue = Number(strValue);
        if (isNaN(numValue) || numValue < 1) {
            isExcelSerial = false;
        }
    }
  }

  if (headerIsDateHint && (isDate || isExcelSerial)) {
      if (isDate) return 'DATE';
      if (isExcelSerial) return 'EXCEL_SERIAL_DATE';
  }

  // Fallback for non-date types
  if (values.every(v => v === null || v === undefined || /^-?\d+$/.test(String(v)))) return 'INTEGER';
  if (values.every(v => v === null || v === undefined || !isNaN(Number(String(v).replace(/[$,]/g, ''))))) return 'NUMERIC';
  return 'TEXT';
}

// --- MAIN LOGIC ---

const items = $input.all();

if (items.length === 0) {
  return [];
}

const headers = Object.keys(items[0].json);
const samples = items.map(item => item.json);
const schema = {};

// First, build the schema by inferring data types for each column
for (const header of headers) {
  const columnValues = samples.map(sample => sample[header]);
  schema[header] = inferDataType(header, columnValues);
}

// Now, clean the data using the schema we just built
const cleanedData = items.map(item => {
  const newRow = {};
  for (const header of headers) {
    const sanitizedHeader = sanitizeName(header);
    const dataType = schema[header];
    let value = item.json[header];

    // UPDATED: Added complete data transformation logic to match the ingestion workflow
    if (dataType === 'EXCEL_SERIAL_DATE' && typeof value === 'number') {
        const date = excelSerialToDate(value);
        value = date ? date.toISOString().split('T')[0] : null; // Format as YYYY-MM-DD
    } else if ((dataType === 'INTEGER' || dataType === 'NUMERIC') && typeof value === 'string') {
      value = Number(String(value).replace(/[$,]/g, ''));
      if (isNaN(value)) value = null;
    } else if (dataType === 'DATE' && value) {
      const date = new Date(value);
      if (!isNaN(date.getTime())) {
        value = date.toISOString();
      } else {
        value = null;
      }
    }

    newRow[sanitizedHeader] = value;
  }
  return { json: newRow };
});

return cleanedData;
